import { useState, useEffect } from "react";

function App() {
  const [list, setList] = useState([]);
  console.log(list);

  const API_BASE = "http://localhost:3001"

  const getList = () => {
    fetch(API_BASE + "/todo")
      .then(res => res.json())
      .then(data => setList(data))
      .catch(err => console.error(err));
  }

  useEffect(() => {
    getList();
  }, []);

  const addItem = (event) => {
    event.preventDefault();
console.log("Clicked");
  }

  const completeItem = async id => {
    const data = await fetch(API_BASE + "/todo/update/" + id, { method: "put" })
      .then(res => res.json());

    setList(list => list.map((item) => {
      if (item._id === data._id) {
        item.isComplete = data.isComplete;
      }

      return item;

    }));
  }


  return (
    <div className="App">
      <h1 className="greeting"> Welcome, Prajanyu </h1>
      <h2 className="title"> Todo List </h2>
      {
        list.map((data) => (
          <div onClick={() => (completeItem(data._id))} className="item-container" key={data._id}>
            <h4 className={(data.isComplete && "complete")}>{data.content}</h4>
            <button>X</button>
          </div>
        ))
      }
      <form onSubmit={addItem}>
        <input type="text" name="item" className="add-text" placeholder="Add Item" />
        <button type="submit" className="add-btn">+</button>
      </form>
    </div>
  );
}

export default App;
