import { useState, useEffect } from "react";

function App() {
  const [list, setList] = useState([]);
  console.log(list);

  const API_BASE = "http://localhost:3001"

  const getList = () => {
    fetch(API_BASE + "/todo")
      .then(res => res.json())
      .then(data => setList(data))
      .catch(err => console.error(err));
  }

  useEffect(() => {
    getList();
  }, []);

  const handleClick = (event) => {
    console.log("clicked");
  }

  const handleComplete = async id => {
    const data = fetch(API_BASE + "/todo/update/" + id)
    console.log(data)
    // .then(res => res.json());

    // setList(list => list.map((item) => {
    //   if (item._id === data._id) {
    //     item.isComplete = data.isComplete;
    //   }

    //   return item;

    // }))
  }


  return (
    <div className="App">
      <h1 className="greeting"> Welcome, Prajanyu </h1>
      <h2 className="title"> Todo List </h2>
      {
        list.map((data) => (
          <div onClick={() => (handleComplete(data._id))} className="item-container" key={data._id}>
            <h4 className={(data.isComplete && "complete")}>{data.content}</h4>
            <button>X</button>
          </div>
        ))
      }

      <button onClick={handleClick} className="add-btn">+</button>
    </div>
  );
}

export default App;
